  const status = data.status || "unknown";
  const smtp_check = data.smtp_check ?? false;
  const gibberish = data.gibberish ?? false;
  const disposable = data.disposable ?? false;
  const block = data.block ?? false;

  // Determine if we can use this email
  let verified = false;

  if (result === "deliverable") {
    verified = true;
  } else if (result === "undeliverable") {
    verified = false;
  } else if (result === "risky") {
    // Lenient filtering: Allow if at least 3 positive checks pass
    const positiveChecks = [smtp_check, !gibberish, !disposable, !block].filter(Boolean).length;
    verified = positiveChecks >= 3;
  }

  return {
    email,
    verified
  };
});

return output;

