const genericEmailPatterns = ["info@", "support@", "help@", "contact@", "hello@", "admin@", "team@"];

// Helper functions
const normalize = str => str?.toLowerCase()?.trim() || "";
const isGenericEmail = email => genericEmailPatterns.some(pattern => email.includes(pattern));

// Scoring function
function scoreContact(contact) {
  const position = normalize(contact.position);
  const department = normalize(contact.department);
  const email = normalize(contact.value);
  const type = normalize(contact.type);

  let score = 0;

  // Add priority scores
  for (const category of priorityCategories) {
    if (
      category.keywords.some(keyword => position.includes(keyword) || department.includes(keyword))
    ) {
      score += category.score;
      break; // Stop at the first matched high-priority category
    }
  }

  // Personal emails get +1, generic get -2
  if (type === "personal") score += 1;
  if (isGenericEmail(email)) score -= 2;

  return { ...contact, score };
}

// Process input
const scored = input.map(scoreContact);
const sorted = scored.sort((a, b) => b.score - a.score);
const topContacts = sorted.slice(0, 4);

// Output
return topContacts.map(c => ({ json: c }));
